import * as React from "react";
import { styled } from "@mui/material/styles";

import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";

import Divider from "@mui/material/Divider";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import CallIcon from "@mui/icons-material/Call";
import Image from "mui-image";

import "@fontsource/roboto/300.css";
import "@fontsource/roboto/400.css";
import "@fontsource/roboto/500.css";
import "@fontsource/roboto/700.css";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#c2c2c2" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export default function AboutUs() {
  return (
    <Box id="home" sx={{ flexGrow: 1 }}>
      <br />
      <Divider>
        <Typography
          sx={{ display: { xs: "none", sm: "none", md: "flex" } }}
          variant="h2"
          color="primary.light"
        >
          Great Detailing
        </Typography>
        <Typography
          sx={{ display: { xs: "none", sm: "flex", md: "none", xl: "none" } }}
          variant="h4"
          color="primary.light"
        >
          Great Detailing
        </Typography>
        <Typography
          sx={{
            display: { xs: "flex", sm: "none", md: "none" },
            fontSize: { xs: "2rem" },
          }}
          variant="h6"
          color="primary.light"
        >
          Great Detailing
        </Typography>
      </Divider>
      <Typography
        sx={{ display: "flex", justifyContent: "center" }}
        variant="h6"
        color="primary.light"
      >
        Amazing Quality Service
      </Typography>

      <Typography
        sx={{
          fontSize: { xs: "1.1rem", md: "2rem" },
          display: "flex",
          justifyContent: "center",
          pl: 2,
          pr: 2,
          pt: 1,
        }}
      >
        At Willis Deluxe Detailing we take pride in the quality of our work.
      </Typography>
      <Typography
        sx={{
          fontSize: { xs: "1.1rem", md: "2rem" },
          display: "flex",
          justifyContent: "center",
          pl: 2,
          pr: 2,
          pt: 1,
          pb: 1,
        }}
      >
        We guarantee you will be satisfied with our service.
      </Typography>

      <Grid
        container
        spacing={2}
        direction="column"
        justifyContent="center"
        alignItems="center"
      >
        <Grid item xs={12} sm={10} md={6} lg={6} xl={6}>
          <Paper elevation={8}>
            <Image src={require("../images/Willis_Deluxe_Detail_j.webp")} />
          </Paper>
        </Grid>
      </Grid>
      {/*//=========== GRID BOX START =================================> */}

      <Grid
        container
        spacing={2}
        direction="row"
        justifyContent="center"
        alignItems="center"
      >
        <Grid item xs={12} sm={10} md={10} lg={10} xl={7}>
          <Item
            elevation={4}
            sx={{ bgcolor: "primary.dark", color: "primary.contrastText" }}
          >
            <Paper sx={{ bgcolor: "primary.light" }}>
              <Divider variant="middle">
                <Typography
                  variant="h6"
                  sx={{
                    fontSize: { xs: "1rem", md: "1.5rem" },
                    fontWeight: 700,
                  }}
                >
                  Our Promise To You
                </Typography>
              </Divider>
            </Paper>
            <Grid
              sx={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "space-evenly",
                bgcolor: "primary.light",
              }}
            ></Grid>
            <Typography>
              When you walk into our shop to have your vehicle detailed, we are
              dedicated to providing you with friendly service, a welcoming
              atmosphere, and above all else, excellent results. If you are not
              satisfied, please let us know and we will do whatever we can to
              make things right!
            </Typography>
            <Button
              bgcolor="secondary.dark"
              href="#appointment"
              startIcon={<CallIcon />}
              variant="contained"
            >
              Schedule An Appointment!
            </Button>
          </Item>
        </Grid>

        {/*//=============== GRID END GOES TO BOTTOM  =====================> */}
      </Grid>
      <br />
    </Box>
  );
}
